#!/usr/bin/env python3
# -*- coding: utf-8 -*-
# https://uva.onlinejudge.org/index.php?option=com_onlinejudge&Itemid=8&category=90&page=show_problem&problem=1104

import sys

def main():
    while True:
        n = int(sys.stdin.readline())
        if n == 0:
            break
        costs = [int(x) for x in sys.stdin.readline().split()]
        costs.sort(reverse=True)
        sum_costs = 0
        for i in range(n):
            sum_costs += costs[i] * (2**i - 1)
        print(sum_costs)

if __name__ == '__main__':
    main()
