def max_fruits(a, b, c):
    if a < b:
        a, b = b, a
    if a < c:
        a, c = c, a
    if b < c:
        b, c = c, b
    if a % 2 == 1:
        a -= 1
    if b % 2 == 1:
        b -= 1
    if c % 4 == 3:
        c -= 1
    return a // 2 + b // 4 + c // 4


