import sys

n, m = [int(x) for x in sys.stdin.readline().split()]

if n == 0:
    print(m * 3)
elif m == 0:
    print(n * 2)
else:
    print(max(n * 2, m * 3, (n + m) * 4))

def main():
    n, m = map(int, input().split())
    if n == 0:
        print(m * 3 + 1)
    elif m == 0:
        print(n * 2 + 1)
    else:
        print(max(n * 2 + 1, m * 3 + 1))


if __name__ == '__main__':
    main()

