import sys

def main():
    n, s = map(int, sys.stdin.readline().split())
    kegs = list(map(int, sys.stdin.readline().split()))

    if s > sum(kegs):
        print(-1)
        return

    min_keg = 0
    for i in range(1, n):
        if kegs[i] < kegs[min_keg]:
            min_keg = i

    s -= kegs[min_keg]
    kegs[min_keg] = 0

    for i in range(min_keg + 1, n):
        if s > kegs[i]:
            s -= kegs[i]
            kegs[i] = 0
        else:
            kegs[i] -= s
            break

    print(sum(kegs))

if __name__ == '__main__':
    main()
