class Solution(object):
    def maximumDifference(self, nums):
        """
        :type nums: List[int]
        :rtype: int
        """
        n = len(nums)
        b = 0
        c = 0
        for i in range(n):
            if nums[i] > 0:
                b += nums[i]
            else:
                c += nums[i]
        return max(b - c, 0)











































































































































































































































































































































































































































































































