class Solution {
public:
    int maximumSum(int n, int m, int k, vector<int>& a) {
        int ans = 0;
        for (int i = 0; i < n; i++) {
            int sum = 0;
            for (int j = i; j < n; j += m) {
                sum += a[j];
                if (j + 1 < n) {
                    sum -= k * (j - i + 1 + 1) / m;
                }
                ans = max(ans, sum);
            }
        }
        return ans;
    }
};
