#include<bits/stdc++.h>
using namespace std;
int main()
{
    int n,k;
    cin>>n>>k;
    int a[n];
    for(int i=0;i<n;i++)
    {
        cin>>a[i];
    }
    int ans=0;
    while(1)
    {
        int flag=0;
        for(int i=0;i<n;i++)
        {
            if(a[i]>k)
            {
                a[i]-=k;
                flag=1;
            }
        }
        if(flag==0)
        {
            break;
        }
        ans++;
    }
    cout<<ans;
    return 0;
}
