import sys

n, d = map(int, sys.stdin.readline().split())
s = sys.stdin.readline().strip()


def jump(n, d, s):
    if n == 1:
        return 0
    if s[n - 1] == '0':
        return -1
    for i in range(n - 1, 0, -1):
        if s[i - 1] == '0':
            continue
        if i - n + 1 <= d:
            return 1 + jump(i, d, s)
    return -1


print(jump(n, d, s))

