#!/usr/bin/env python3

import sys

n, k = map(int, sys.stdin.readline().split())
a = list(map(int, sys.stdin.readline().split()))


def check(a, n, k):
    for i in range(1, n):
        if a[i] < a[i - 1] + k:
            return False
    return True


def solve(a, n, k):
    if check(a, n, k):
        return 0
    for i in range(n):
        a[i] += 1
        if check(a, n, k):
            return 1 + solve(a, n, k)
        a[i] -= 1
    return -1


print(solve(a, n, k))
for i in range(n):
    print(a[i], end=" ")
