def get_max_good_string_length(a, b, c):
    if a == 0 and b == 0 and c == 0:
        return 0
    elif a == 0 and b == 0 and c != 0:
        return c
    elif a == 0 and b != 0 and c == 0:
        return b
    elif a == 0 and b != 0 and c != 0:
        return max(b, c)
    elif a != 0 and b == 0 and c == 0:
        return a
    elif a != 0 and b == 0 and c != 0:
        return max(a, c)
    elif a != 0 and b != 0 and c == 0:
        return max(a, b)
    else:
        return max(get_max_good_string_length(a - 1, b, c),
                   get_max_good_string_length(a, b - 1, c),
                   get_max_good_string_length(a, b, c - 1),
                   get_max_good_string_length(a - 1, b - 1, c),
                   get_max_good_string_length(a - 1, b, c - 1),
                   get_max_good_string_length(a, b - 1, c - 1),
                   get_max_good_string_length(a - 1, b - 1, c - 1))

