import math

def is_k_regular(k):
    if k == 1:
        return True
    if k % 2 != 0:
        return False
    n = int(math.sqrt(k))
    if n * n != k:
        return False
    for i in range(1, n):
        if i * n + 1 != k:
            return False
    return True

def is_k_regular_graph(k):
    if k == 1:
        return True
    if k % 2 != 0:
        return False
    n = int(math.sqrt(k))
    if n * n != k:
        return False
    for i in range(1, n):
        if i * n + 1 != k:
            return False
    for i in range(1, n):
        for j in range(1, n):
            if i * n + j + 1 != k:
                return False
    return True

