class Solution(object):
    def numberOfWays(self, n, k, d):
        """
        :type n: int
        :type k: int
        :type d: int
        :rtype: int
        """
        dp = [[0] * (k + 1) for _ in range(n + 1)]
        dp[0][0] = 1
        for i in range(1, n + 1):
            for j in range(1, k + 1):
                dp[i][j] = (dp[i][j - 1] + dp[i - 1][j]) % 1000000007
                if j >= d:
                    dp[i][j] += dp[i - 1][j - d]
                    dp[i][j] %= 1000000007
        return dp[n][k]
