def main():
    n, a, b = map(int, input().split())
    if b == 0:
        print(a)
    elif b > 0:
        if a + b <= n:
            print(a + b)
        else:
            print(a + b - n)
    else:
        if a + b >= 1:
            print(a + b + n)
        else:
            print(a + b + n - 1)


if __name__ == '__main__':
    main()
