import sys

d, k, a, b, t = map(int, sys.stdin.readline().split(' '))

def drive(d, k, a, b, t):
    if d <= k:
        return d * a
    else:
        return (d - k) * a + t + drive(k, k, a, b, t)

def walk(d, k, a, b, t):
    if d <= k:
        return d * b
    else:
        return (d - k) * b + t + walk(k, k, a, b, t)

def repair(d, k, a, b, t):
    if d <= k:
        return 0
    else:
        return t + repair(k, k, a, b, t)

print(min(drive(d, k, a, b, t), walk(d, k, a, b, t), repair(d, k, a, b, t)))

































































































































































































































