def get_min_distance(x1, x2, x3):
    """

    :param x1:
    :param x2:
    :param x3:
    :return:
    """

    if x1 == x2 or x2 == x3 or x1 == x3:
        return 0

    if x1 > x2:
        x1, x2 = x2, x1

    if x1 > x3:
        x1, x3 = x3, x1

    if x2 > x3:
        x2, x3 = x3, x2

    return min(abs(x1 - x2), abs(x2 - x3), abs(x1 - x3))


def main():

    x1, x2, x3 = list(map(int, input().split()))

    print(get_min_distance(x1, x2, x3))


if __name__ == '__main__':
    main()





























