#include<bits/stdc++.h>
using namespace std;
int main()
{
    int n;
    cin>>n;
    int a[n+1];
    for(int i=1;i<=n;i++)
    {
        cin>>a[i];
    }
    int ans=0;
    for(int i=1;i<=n;i++)
    {
        int sum=0;
        for(int j=i;j<=n;j++)
        {
            sum+=a[j];
            if(j-i+1>ans)
            {
                ans=j-i+1;
            }
        }
    }
    cout<<ans;
    return 0;
}

class Solution(object):
    def maxAverageRatio(self, nums):
        """
        :type nums: List[int]
        :rtype: float
        """
        n = len(nums)
        if n == 1:
            return nums[0]
        if n == 2:
            return max(nums[0], nums[1])
        nums.sort()
        ans = 0
        for i in range(n - 1):
            ans = max(ans, nums[i + 1] / (n - i - 1) - nums[i] / (n - i))
        return ans
