    t = int(sys.stdin.readline().strip())
    for _ in range(t):
        n = int(sys.stdin.readline().strip())
        a = [int(x) for x in sys.stdin.readline().strip().split()]
        while len(a) > 1:
            i = 0
            while i < len(a) - 1 and a[i] == a[i + 1]:
                i += 1
            if i == len(a) - 1:
                break
            a[i] += a[i + 1]
            a.pop(i + 1)
            a.pop(i)
        print(len(a))

