#!/usr/bin/env python3

import sys

n, k = map(int, sys.stdin.readline().split())

diplomas = 0
certificates = 0
winners = 0

for i in range(1, n + 1):
    if i % k == 0:
        certificates += 1
    else:
        diplomas += 1

    if i % (k + 1) == 0:
        winners += 1

print(diplomas, certificates, winners)


















































































































































































































































































































































































































