def main():
    n, k = [int(x) for x in input().split()]
    diplomas = 0
    certificates = 0
    winners = 0
    for i in range(1, n + 1):
        if i % k == 0:
            certificates += 1
        else:
            diplomas += 1
    if (certificates + diplomas) % 2 == 0:
        winners = (certificates + diplomas) // 2
    else:
        winners = (certificates + diplomas) // 2 + 1
    print(diplomas, certificates, winners)


if __name__ == '__main__':
    main()










































































































































































































































































































































































