class Solution(object):
    def canBeSorted(self, nums, swaps):
        """
        :type nums: List[int]
        :type swaps: str
        :rtype: bool
        """
        n = len(nums)
        for i in range(n):
            for j in range(i + 1, n):
                if swaps[i] == '1' and nums[i] > nums[j]:
                    nums[i], nums[j] = nums[j], nums[i]
                    return self.canBeSorted(nums, swaps)
        return True

