#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main()
{
    int n, k;
    cin >> n >> k;
    vector<int> light;
    for (int i = 0; i < n; i++)
    {
        int temp;
        cin >> temp;
        light.push_back(temp);
    }
    vector<int> hours;
    for (int i = 0; i < k; i++)
    {
        int temp;
        cin >> temp;
        hours.push_back(temp);
    }
    sort(light.begin(), light.end());
    sort(hours.begin(), hours.end());
    int min_light = light[hours[0] - 1];
    for (int i = 0; i < k; i++)
    {
        min_light = min(min_light, light[hours[i] - 1]);
    }
    cout << min_light << endl;
    for (int i = 0; i < k; i++)
    {
        cout << hours[i] << " ";
    }
    return 0;
}

def read_input():
    n, k = map(int, input().split())
    a = list(map(int, input().split()))
    return n, k, a


def solve(n, k, a):
    a.sort()
    b = []
    for i in range(k):
        b.append(a[i])
    return min(b), b


n, k, a = read_input()
ans = solve(n, k, a)
print(ans[0])
print(' '.join(map(str, ans[1])))



















































































































































































































































































































