#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
const int N=1e5+10;
int t,n,x,cnt0,cnt1;
string s;
int main(){
    cin>>t;
    while(t--){
        cin>>n>>x;
        cin>>s;
        cnt0=cnt1=0;
        for(int i=0;i<n;i++){
            if(s[i]=='0')cnt0++;
            else cnt1++;
        }
        if(abs(cnt0-cnt1)!=x)cout<<-1<<endl;
        else{
            int ans=0;
            for(int i=0;i<n;i++){
                if(s[i]=='0')cnt0--;
                else cnt1--;
                if(abs(cnt0-cnt1)==x)ans++;
            }
            cout<<ans<<endl;
        }
    }
    return 0;
}
