    {
        if(s[i]=='[')
        {
            int j=i+1;
            int cnt=0;
            while(s[j]!=':')
            {
                if(s[j]=='|')
                    cnt++;
                j++;
            }
            if(cnt==0)
            {
                cout<<-1;
                return 0;
            }
            else
            {
                int k=j+1;
                while(s[k]!=']')
                {
                    if(s[k]=='|')
                        cnt--;
                    k++;
                }
                if(cnt==0)
